//import SpotifyPlayer from 'react-spotify-web-playback'
import ReactPlayer from 'react-player/youtube'
import {useEffect, useState} from 'react'
const BaseURL = 'https://www.youtube.com/watch?v='
const KEY = "AIzaSyB9MUudXyynejMUw_XZQus3djYO54hq2lY";
const baseURLGoogle = `https://www.googleapis.com/youtube/v3/search?key=${KEY}&type=video&part=snippet&maxResults=1&q=`;

export default function Player({ playingTrack }) {
  const [urls, setUrls] = useState([]);
  console.log(urls);
  
  useEffect(() => {
    if(playingTrack)
    setUrls([`${BaseURL}${playingTrack}`]);
  }, [playingTrack])
  
  return (
    <ReactPlayer 
      url={urls} 
      playing={true}
      loop={false}
      width='100%'
      height='100%'
    />
  )
}
