import React from 'react'
import Search from './Search'
import {useState, useEffect} from 'react'

export default function TrackSearchResult({ track, handleYoutubeSearch }) { 
    const [isSearching, setIsSearching] = useState(false);
    
    useEffect(() => {
        const videoId = Search(track.title)
        handleYoutubeSearch(videoId);
        setIsSearching(false);
    }, [isSearching, videoId] );
    
    function searchForTrack() {   
        if(isSearching % 2 === 1) return;
        setIsSearching(true);   
    }
    return (
        <div className="d-flex m-2 align-items-center" 
            style={{cursor: 'pointer'}}
            onClick={searchForTrack}>
            <img src={track.albumUrl} style={{height : '64px', width:'64px'}}></img>
            <div className="ms-3">
                <div>{track.title}</div>
                <div className="text-muted">{track.artist}</div>
            </div>
        </div>
    )
}
