import React, { useCallback } from 'react'
import Search from './Search'
import { useState, useEffect, useRef} from 'react';
import axios from 'axios';
const KEY = "AIzaSyB9MUudXyynejMUw_XZQus3djYO54hq2lY";
const baseURLGoogle = `https://www.googleapis.com/youtube/v3/search?key=${KEY}&type=video&part=snippet&maxResults=1&q=`;

export const useIsMounted = () => {
    const mountedRef = useRef(false);
    const isMounted = useCallback(() => mountedRef.current, []);
    
    useEffect(() => {
        mountedRef.current = true;
        return () => {
        mountedRef.current = false;
        };
    });
    return isMounted;
};

export default function TrackSearchResult({ track, handleYoutubeSearch }) { 
    const isMounted = useIsMounted();
    const [searching, setSearching] = useState(false);   
    const [playingTrack, setPlayingTrack] = useState("");
    
    useEffect(() => {
        if(!searching) return;
        const videoId = () => {
            axios
            .get(`${baseURLGoogle}${track.title}`)
            .then(res => {
                setPlayingTrack(res.data.items[0].id.videoId);  
            })
            .catch(err => {
                console.error(err)
            })
        }      
        if(playingTrack) handleYoutubeSearch(videoId);
    }, [searching, playingTrack])
    
    // function searchForTrack() {       
    //     const videoId = search;
    //     handleYoutubeSearch(videoId);
    // }
    return (
        <div className="d-flex m-2 align-items-center" 
            style={{cursor: 'pointer'}}
            onClick={() => setSearching(true)}>
            <img src={track.albumUrl} style={{height : '64px', width:'64px'}}></img>
            <div className="ms-3">
                <div>{track.title}</div>
                <div className="text-muted">{track.artist}</div>
            </div>
        </div>
    )
}
