import axios from "axios";

const KEY = "AIzaSyCl31opB_mjpKlTNGHjStEcpDiC_bhPzaI";
const baseURL = `https://www.googleapis.com/youtube/v3/search?key=${KEY}&type=video&part=snippet&maxResults=1&q=`;

export default function Search(keyword) {
  axios
    .get(`${baseURL}${keyword}`)
    .then((res) => {
      if (res.data.items !== undefined) {
        waitFor(res.data.items, () => {
          return res.data.items;
        });
      }
    })
    .catch((err) => {
      console.log(err);
    });
}
function waitFor(variable, callBack) {
  var interval = setInterval(() => {
    if (window[variable]) {
      clearInterval(interval);
      callBack();
    }
  }, 1000);
}
